WITH bowlerStats AS (SELECT bowler, SUM(total_runs) AS totalRuns, SUM(bye_runs) AS byeRuns, SUM(legbye_runs) AS legByeRuns, SUM(CASE WHEN wide_runs = 0 AND noball_runs = 0 THEN 1 ELSE 0 END) AS totalBalls FROM deliveries INNER JOIN matches ON deliveries.match_id = matches.id WHERE matches.season = 2015 GROUP BY bowler) SELECT bowler, ROUND(SUM(totalRuns - byeRuns - legByeRuns) * 6.0 / SUM(totalBalls), 2) AS economyRate FROM bowlerStats GROUP BY bowler ORDER BY economyRate ASC LIMIT 10;