With batsmanStats AS (SELECT m.season, d.batsman, SUM(d.batsman_runs) AS batsmanRuns, SUM(CASE WHEN d.wide_runs = 0 AND d.noball_runs = 0 THEN 1 ELSE 0 END) AS batsmanBalls FROM deliveries d INNER JOIN matches m ON m.id = d.match_id WHERE d.batsman = 'MS Dhoni' GROUP BY m.season, d.batsman) SELECT season, batsman, ROUND((batsmanRuns*100.00)/batsmanBalls, 2) AS strikeRate FROM batsmanStats ORDER BY season;
